"use strict";(self.webpackChunkudonarium=self.webpackChunkudonarium||[]).push([[2717],{6098:n=>{n.exports=JSON.parse('{"ko_kr":{"SkynautsBouken":{"special":"\uc2a4\ud398\uc15c(\u3010\uc0dd\uba85\uc810\u30111d6\ud68c\ubcf5)","fumble":"\ud38c\ube14(\ud38c\ube14\ud45cFT)","big_shake":"\uc9c4\ub3d9","fire":"\ud654\uc7ac","ballistics":"\ud0c4\ub3c4\ud559","avoidance":"\ud68c\ud53c\uc6b4\ub3d9","y":"\uc138\ub85c","x":"\uac00\ub85c","directions":{"lower_left":"\uc88c\ud558","lower":"\ud558","lower_right":"\uc6b0\ud558","left":"\uc88c","right":"\uc6b0","upper_left":"\uc88c\uc0c1","upper":"\uc0c1","upper_right":"\uc6b0\uc0c1"},"FumbleTable":{"name":"\ud38c\ube14\ud45c","type":"1D6","items":["\uac04\uc2e0\ud788 \ub300\ud615 \uc0ac\uace0\ub294 \uba74\ud588\ub2e4. \ud3c9\ubc94\ud55c \uc2e4\ud328\uc640 \ub611\uac19\uc774 \ucc98\ub9ac\ud55c\ub2e4.","\ub118\uc5b4\uc9c0\uace0 \ub9d0\uc558\ub2e4. \ub2f9\uc2e0\uc740 [\ub118\uc5b4\uc9d0]\uc774 \ub41c\ub2e4.","\uc2e4\ud328\ud574\uc11c \uc9dc\uc99d\uc744 \ub0b4\uace0 \ub9d0\uc558\ub2e4. \ub2f9\uc2e0\uc774 \ud68d\ub4dd\ud55c \u3010\uc720\ub300\u3011 \ud558\ub098\uc758 \u300c\uc9c0\uc6d0 \uccb4\ud06c\u300d\ub97c \uccb4\ud06c\ud55c\ub2e4.","\ubc29\uc744 \ubd80\uc218\uace0 \ub9d0\uc558\ub2e4. \uc790\uc2e0\uc774 \uc788\ub294 \uce78\uc744 [\ud30c\uc190]\uc2dc\ud0a8\ub2e4. \uc774 [\ud30c\uc190]\uc73c\ub85c\ub294 \ub2f9\uc2e0\uc758 \u3010\uc0dd\uba85\uc810\u3011\uc774 \uac10\uc18c\ud558\uc9c0 \uc54a\ub294\ub2e4.","\uba38\ub9ac\ub97c \ubd80\ub52a\ud788\uace0 \ub9d0\uc558\ub2e4. \ub2f9\uc2e0\uc758 \u3010\uc0dd\uba85\uc810\u3011\uc774 \u300c1D6\u300d\uc810 \uac10\uc18c\ud55c\ub2e4.","\uae30\uc801\uc801\uc778 \uacb0\uacfc. \uc774 \ud589\ub3d9 \ud310\uc815\uc740 \uc131\uacf5\ud55c\ub2e4."]},"VoyageTable":{"name":"\ud56d\ud589\ud45c","type":"1D6","items":["\uc2a4\ud3ff 1\uce78 \uc774\ub3d9","\uc2a4\ud3ff 1\uce78 \uc774\ub3d9","\uc2a4\ud3ff 1\uce78 \uc774\ub3d9","\uc2a4\ud3ff 2\uce78 \uc774\ub3d9","\uc2a4\ud3ff 2\uce78 \uc774\ub3d9","\uc2a4\ud3ff 3\uce78 \uc774\ub3d9"]},"NavigationEventNavigationTable":{"name":"\ud56d\ud589\uc774\ubca4\ud2b8\ud45c \ud56d\ud589\uacc4","type":"2D6","items":["\ubc30\uba40\ubbf8\\n \ubc30\ub4dc \uc2a4\ud14c\uc774\ud130\uc2a4. \ub2f9\uc2e0\uc740 \ub2e4\uc74c \ud018\uc2a4\ud2b8 \ud398\uc774\uc988\ub97c \uc885\ub8cc\ud560 \ub54c\uae4c\uc9c0 \u3010\uc774\ub3d9\ub825\u3011\uc774 \u300c1\uc810\u300d \ub0ae\uc544\uc9c0\uba70, \u300c\ub300\uc2dc\u300d\ub97c \ud560 \uc218 \uc5c6\ub2e4.\\n\ud558\ub298 \uc704\uc5d0\uc11c \ubc14\ub78c\uc5d0 \ud754\ub4e4\ub9ac\ub2e4 \ubcf4\uba74 \ub9c8\uce58 \uc790\uc2e0\uacfc \uc138\uacc4\uac00 \ub4a4\uc11e\uc778 \uac83 \uac19\uc740 \ub290\ub08c\uc774 \ub4e0\ub2e4. \uadf8\uac83\uc744 \ub5a0\uc62c\ub9ac\uae30\ub9cc \ud574\ub3c4\u2026 \uc6b0\uc5d1.","\ub09c\ub958\\n \ub2e4\uc74c \uc7a5\uba74\uc5d0\uc11c\ub294 \ud56d\ud589 \ud45c\uc758 \uacb0\uacfc\uc640 \uad00\uacc4\uc5c6\uc774 \ud56d\ud589 \uccb4\ud06c\ub85c \ube44\uacf5\uc815 \ub9d0\uc774 \ub098\uc544\uac00\ub294 \uac70\ub9ac\uac00 \uc2a4\ud3ff 1\uce78\uc774 \ub41c\ub2e4.\\n\uc774 \uadfc\ucc98\ub294 \uae30\ub958\uac00 \uc5c9\ub9dd\uc9c4\ucc3d\uc774\ub2e4. \uc774\ub798\uc11c\uc57c \ubc14\ub78c\uc744 \ud0c0\uace0 \ubc30\ub97c \ubaa8\ub294 \uac83\uc740 \ubd88\uac00\ub2a5\ud558\ub2e4. \ucd08\uc870\ud574\ud558\uc9c0 \ub9d0\uace0 \uc2e0\uc911\ud558\uac8c \ub098\uc544\uac00\uc57c \ud55c\ub2e4.","\uae09\ubcc0 GM\uc740 \ud50c\ub77c\uc774\ud2b8 \uc5d0\uc5b4\ub9ac\uc5b4 \uc0c1\uc5d0\uc11c \uc784\uc758\uc758 \uc2a4\ud3ff \ub450 \uad70\ub370\ub97c \uc120\ud0dd\ud55c\ub2e4. \uadf8 \uc2a4\ud3ff\uc758 \uc774\ubca4\ud2b8 \ud0c0\uc785\uc740 \u300c\uace4\ub780\uacc4\u300d\ub85c \ubcc0\uacbd\ub41c\ub2e4.\\n\ud558\ub298\uc758 \ub0a0\uc528\ub294 \ubcc0\ub355\uc2a4\ub7fd\ub2e4. \ub0a0\uc528\uac00 \ubc14\ub00c\ub294 \uac83\uc744 \ub9c9\uc744 \ubc29\ubc95\uc740 \uc5c6\ub2e4. \ud558\uc9c0\ub9cc \uc774\ub7f0 \uc2dd\uc73c\ub85c \ubcc0\ub355\uc744 \ubd80\ub9ac\uc9c0\ub294 \uc54a\uc558\uc73c\uba74 \ud588\ub294\ub370\u2026\u2026.","\uc545\ucc9c\ud6c4\\n\ub2e4\uc74c \uc7a5\uba74\uc5d0\uc11c\ub294 \uc7a5\uba74 \ud50c\ub808\uc774\uc5b4\uac00 \ud56d\ud589 \uccb4\ud06c\ub97c \ud560 \uc218 \uc5c6\uc73c\uba70, \u300c\uc870\ud0c0\uc5d0 \uc804\ub150\ud558\uae30\u300d\ub97c \uc120\uc5b8\ud560 \uc218\ub3c4 \uc5c6\ub2e4.\\n\ub611, \ub611, \ud558\uace0 \ucc3d\uc5d0 \ube57\ubc29\uc6b8\uc774 \ub5a8\uc5b4\uc9c4\ub2e4. \uc774\ub807\uac8c \ub418\uba74 \ubc30\ub97c \ubab0 \uc218 \uc5c6\ub2e4. \uc774 \ud2c8\uc5d0 \ud64d\ucc28\ub77c\ub3c4 \ud55c \uc794 \ud558\ub294 \uac83\ub3c4 \uad1c\ucc2e\uaca0\uc9c0. \ud2f0\ud0c0\uc784\uc5d0\ub294 \ucd5c\uc545\uc778 \ub0a0\uc528\uc9c0\ub9cc.","\uae30\ub958-1\\n\ube44\uacf5\uc815 \ub9d0\uc744 1\uc2a4\ud3ff \ub418\ub3cc\ub9b0\ub2e4.\\n\ube44\uacf5\uc815\uc744 \ud5a5\ud574 \ubd80\ub294 \uae30\ub958\uac00 \ud56d\uc0c1 \ud0d0\uacf5\uc0ac\ub4e4\uc758 \ud3b8\uc774\ub77c\ub294 \ubc95\uc740 \uc5c6\ub2e4. \ub204\uad70\uac00\uc758 \ub4a4\ub97c \ubc00\uc5b4\uc8fc\ub294 \ubc14\ub78c\uc774 \ub204\uad70\uac00\uc5d0\uac8c\ub294 \uc5ed\ud48d\uc77c \ub54c\ub3c4 \uc788\uc744 \ud14c\uc9c0.","\ub9d1\uc740 \ud558\ub298\\n\uc774 \ud56d\ud589 \uc774\ubca4\ud2b8\uc5d0\ub294 \uc544\ubb34\ub7f0 \ud6a8\uacfc\ub3c4 \uc5c6\ub2e4.\\n\ud0c1 \ud2b8\uc778 \uacf5\uc5ed. \ud48d\ud5a5\ub3c4 \uc548\uc815\uc801\uc774\uace0, \ud0a4\ub97c \uaebe\uc744 \ub9cc\ud55c \uc77c\ub3c4 \uc5c6\uc744 \uac83\uc774\ub2e4. \uc544\ub291\ud55c \ud478\ub978\uc0c9 \ud558\ub298 \uc18d\uc5d0\uc11c \ud558\uc580 \uad6c\ub984 \uc0ac\uc774\ub97c \ub204\ube44\ub4ef\uc774 \ubc30\uac00 \uc804\uc9c4\ud55c\ub2e4.","\uae30\ub958+1\\n\ube44\uacf5\uc815 \ub9d0\uc744 1\uc2a4\ud3ff \uc804\uc9c4\uc2dc\ud0a8\ub2e4.\\n\ub2e4\ub978 \uacf5\uc5ed\ubcf4\ub2e4\ub3c4 \ube44\uad50\uc801 \uac15\ud55c \uae30\ub958\uac00 \uc77c\uc815\ud55c \ubc29\ud5a5\uc744 \ud5a5\ud574 \uacc4\uc18d\ud574\uc11c \ubd88\uace0 \uc788\ub294 \uacf5\uc5ed. \ub54c\ub85c\ub294 \ud0d0\uacf5\uc0ac\ub4e4\uc744 \ub3c4\uc640\uc8fc\uace0, \ub54c\ub85c\ub294 \ud0d0\uacf5\uc0ac\ub4e4\uc758 \uc55e\uae38\uc744 \uac00\ub85c\ub9c9\ub294\ub2e4. \uc5b4\ub290 \uacf5\uc5ed\uc5d0 \uc5b4\ub5a4 \ubc14\ub78c\uc774 \ubd80\ub294\uc9c0 \ud30c\uc545\ud574\uc11c \ub098\uc544\uac00\ub294 \uac83\uc774 \ud558\ub298 \uc5ec\ud589\uc758 \uae30\ubcf8\uc774\ub2e4.","\uc9c4\ub85c \uc591\ud638!\\n\ube44\uacf5\uc815\uc740 \u3010\ub2e8\uc11c\u3011\ub97c \u300c1\uac1c\u300d \uc5bb\ub294\ub2e4.\\n\uc774 \uadfc\ucc98\ub294 \ub9e4\uc6b0 \uc804\ub9dd\uc774 \uc88b\ub2e4. \uc6b4\ud574 \uc704\ub85c\ub294 \uad6c\ub984 \ud558\ub098 \ubcf4\uc774\uc9c0 \uc54a\uace0, \uac70\ucd94\uc7a5\uc2a4\ub7ec\uc6b4 \ubd80\uc720\uc0dd\ubb3c\ub4e4\ub3c4 \uc5c6\ub2e4. \uc774 \ud2c8\uc5d0 \uc8fc\ubcc0\uc758 \uc0c1\ud669\uc744 \ud655\uc778\ud574\uc11c \uc55e\uae38\uc5d0 \ub300\ube44\ud558\uc790.","\ub3cc\ud48d+1\\n\ube44\uacf5\uc815 \ub9d0\uc744 1\uc2a4\ud3ff \uc804\uc9c4\uc2dc\ud0a8\ub2e4. \uadf8 \ud6c4, \ube44\uacf5\uc815 \ub9d0\uc774 \uc788\ub294 \uc2a4\ud3ff\uc758 \uc774\ubca4\ud2b8 \ud0c0\uc785\uc744 \ud655\uc778\ud558\uace0, \uadf8 \uc774\ubca4\ud2b8 \ud0c0\uc785\uc758 \ud56d\ud589 \uc774\ubca4\ud2b8 \ud45c\uc5d0\uc11c \uc8fc\uc0ac\uc704\ub97c \uad74\ub824 \ub098\uc628 \ud56d\ud589 \uc774\ubca4\ud2b8\uc758 \ud6a8\uacfc\ub97c \uc801\uc6a9\ud55c\ub2e4.\\n\uac11\uc790\uae30 \ubc30\uc5d0 \uac15\ud48d\uc774 \ubd88\uc5b4\ub2e5\uce5c\ub2e4. \uc5b4\ub5bb\uac8c\ub4e0 \uc804\ubcf5\ub418\uc9c0 \uc54a\uace0 \ubc30\ub97c \uc548\uc815\uc2dc\ucf30\uc9c0\ub9cc, \uc608\uc815\ud55c \ucf54\uc2a4\ub97c \uc774\ud0c8\ud574\ubc84\ub9b0 \uac83 \uac19\ub2e4. \ubb34\uc2a8 \uc77c\uc774 \uc77c\uc5b4\ub0a0\uc9c0\ub3c4 \uc54c \uc218 \uc5c6\ub2e4. \uacbd\uacc4\ub97c \uac15\ud654\ud558\ub294 \uac83\uc774 \uc88b\uc744 \ud14c\uc9c0.","\ubb34\uc5ed\ud48d\\n\ub2e4\uc74c \uc7a5\uba74\uc758 \ud56d\ud589 \uccb4\ud06c\uc5d0\uc11c\ub294 \uc8fc\uc0ac\uc704\ub97c \uad74\ub9ac\uc9c0 \uc54a\uace0, \ud56d\ud589 \ud45c\uc758 \uacb0\uacfc \uc911\uc5d0\uc11c \ub9c8\uc74c\uc5d0 \ub4dc\ub294 \uac83\uc744 \uc120\ud0dd\ud574\uc11c \uc801\uc6a9\ud560 \uc218 \uc788\ub2e4.\\n\uae30\ub958\uac00 \ubd80\ub294 \uacf5\uc5ed \ub0b4\uc5d0\uc11c\ub3c4 \ud2b9\ud788 \ud48d\ud5a5\uc774 \uc548\uc815\uc801\uc774\ub77c \ube44\uacf5\uc815 \uc870\uc885\uc0ac\ub4e4\uc774 \ud56d\uacf5\ub85c\ub85c \uc990\uaca8 \uc0ac\uc6a9\ud558\ub294 \uacf5\uc5ed. \ube44\ud589 \uc624\uc9d5\uc5b4 \uac19\uc740 \uc18c\ud615 \ubd80\uc720\uc0dd\ubb3c\ub3c4 \uace7\uc798 \ubb34\ub9ac\uc9c0\uc5b4 \ub3cc\uc544\ub2e4\ub2cc\ub2e4.","\uae30\ub958+2\\n\ube44\uacf5\uc815 \ub9d0\uc744 2\uc2a4\ud3ff \uc804\uc9c4\uc2dc\ud0a8\ub2e4.\\n\uae30\ub958\ub9cc \uc798 \ud0c0\ub3c4 \ube44\uacf5\uc815\uc740 \uc131\ub2a5 \uc774\uc0c1\uc73c\ub85c \uc6c0\uc9c1\uc5ec\uc900\ub2e4. \uc9c4\ub85c \uc591\ud638. \uc774\ub807\uac8c\ub9cc \uac00\uc900\ub2e4\uba74 \uc608\uc815\ubcf4\ub2e4 \ube68\ub9ac \ubaa9\uc801\uc9c0\uc5d0 \ub3c4\ucc29\ud560 \uc218 \uc788\uc744 \uac83\uc774\ub2e4."]},"NavigationEventEncounterTable":{"name":"\ud56d\ud589\uc774\ubca4\ud2b8\ud45c \uc870\uc6b0\uacc4","type":"2D6","items":["\uac10\uae30\\n\ubc30\ub4dc \uc2a4\ud14c\uc774\ud130\uc2a4. \ub2f9\uc2e0\uc740 \ub2e4\uc74c \ud018\uc2a4\ud2b8 \ud398\uc774\uc988\ub97c \uc885\ub8cc\ud560 \ub54c\uae4c\uc9c0 \ubaa8\ub4e0 [\ud2b9\ud654]\ud55c \u3010\ub2a5\ub825\u3011\uc744 [\ud2b9\ud654]\ud558\uc9c0 \uc54a\uc740 \uac83\uc73c\ub85c \uac04\uc8fc\ud55c\ub2e4.\\n\ucd94\uc6b4 \ud558\ub298 \uc704\uc5d0\uc11c \ub9dd\uc744 \ubd24\ub358 \uac83\uc774 \ubb38\uc81c\uc600\uc744\uae4c? \uc544\ub2c8\uba74 \uc2dd\uc0ac? \uac10\uae30\uc5d0 \uac78\ub9b4 \uac70\ub77c\uba74 \ucc28\ub77c\ub9ac \uc5ec\ud589\uc744 \ub5a0\ub098\uae30 \uc804\uc5d0 \uac78\ub9ac\ub294 \uac8c \ub098\uc558\uc744 \ud150\ub370.","\uc801\ub300\uc801\uc778 \ud558\ub298\uace0\ub798\\n\ube44\uacf5\uc815\uc740 \u300cD46[\uc9c4\ub3d9]/1\u300d\uc758 \ub300\ubbf8\uc9c0\ub97c \uc785\ub294\ub2e4.\\n\ube44\uacf5\uc815 \uc870\uc885\uc0ac \uc0ac\uc774\uc5d0\uc11c \uc778\uae30\uac00 \uc788\ub294 \ud558\ub298\uace0\ub798\uc9c0\ub9cc, \uae30\ubd84\uc774 \uc548 \uc88b\uc744 \ub54c\ub294 \uc788\ub294 \ubc95\uc774\ub2e4. \ud558\ud544 \uadf8\ub54c \ub2e4\uac00\uac14\ub2e4\uba74 \uc815\ub9d0 \uc6b4\uc774 \uc5c6\uc5c8\ub2e4\uace0\ubc16\uc5d0 \ud560 \ub9d0\uc774 \uc5c6\ub2e4. \ub098\ubb34\uc544\ubbf8\ud0c0\ubd88.","\ud2b8\ub7ec\ube14 \uba54\uc774\ucee4\\n\ub2f9\uc2e0\uc758 \u3010\uc0dd\uba85\uc810\u3011\uc774 \u300c3\uc810\u300d \uac10\uc18c\ud55c\ub2e4.\\n\uc6c3\uc5b4\ub118\uae38 \uc77c\uc774 \uc544\ub2c8\uc57c! \uc774\uac8c \ub300\uccb4 \ubb34\uc2a8 \uc9d3\uc774\uc57c!","\uc801\ub300\uc801\uc778 \ubd80\uc720\uc0dd\ubb3c\\n\ube44\uacf5\uc815\uc740 \u300cD/3\u300d\uc758 \ub300\ubbf8\uc9c0\ub97c \uc785\ub294\ub2e4.\\n\ubd80\uc720\uc0dd\ubb3c\uc774 \ud56d\uc0c1 \ube44\uacf5\uc815\uc5d0\uac8c \uc6b0\ud638\uc801\uc774\ub77c\ub294 \ubc95\uc740 \uc5c6\ub2e4. \uadf8\ub4e4\uc5d0\uac8c\ub3c4 \uadf8\ub4e4 \ub098\ub984\ub300\ub85c \uc790\uae30\ub124 \uc0dd\ud0dc\uacc4\uc5d0 \ub6f0\uc5b4\ub4e0 \ubc30\ub97c \ub9de\uc774\ud558\ub294 \ubc29\uc2dd\uc774 \uc788\ub2e4.","\uac80\ubb38\\n\uc774 \uc7a5\uba74\uc5d0\uc11c \ub2f9\uc2e0\uc740 \uc561\uc158\uc744 \ud560 \uc218 \uc5c6\ub2e4.\\n\uc5ec\ub7ec\ubd84\uc758 \ubc30 \uc704\ub85c \uac70\ub300\ud55c \uadf8\ub9bc\uc790\uac00 \ub4dc\ub9ac\uc6cc\uc9c4\ub2e4. \uc5ec\ub7ec\ubd84\uc758 \ubc30\ubcf4\ub2e4 \uba87 \ubc30\ub294 \ucee4 \ubcf4\uc774\ub294 \uc655\ub9bd \uacf5\uad70\uc758 \uc21c\uacf5\ud568\uc774\ub2e4. \u300c\uc5ec\uc655\ud3d0\ud558 \ub9cc\uc138!\u300d \ubd89\uc740 \uc637\uc758 \uad70\uc778\ub4e4\uc774 \uacbd\ub840\uc640 \ud568\uaed8 \uc5ec\ub7ec\ubd84\uc758 \uc11c\ub958 \uc5c5\ubb34\ub97c \ub298\ub824\ub098\uac04\ub2e4. \ub530\ub85c \ud558\uace0 \uc2f6\uc740 \uc77c\uc774 \uc788\uc5c8\ub294\ub370\u2026.","\uc544\ub984\ub2e4\uc6b4 \uacbd\uce58\\n\uc120\ub0b4 \ub9f5\uc0c1\uc5d0 \uc788\ub294 \ubaa8\ub4e0 \uce90\ub9ad\ud130\ub294 \u3010\uc0dd\uba85\uc810\u3011\uc774 \u300c1\uc810\u300d \ud68c\ubcf5\ud55c\ub2e4.\\n \ud558\ub298 \uc704\ub97c \uc5ec\ud589\ud558\ub2e4 \ubcf4\uba74 \uc0c1\uc0c1\uc744 \ucd08\uc6d4\ud558\ub294 \uacbd\uce58\ub97c \ubcf4\uac8c \ub420 \uae30\ud68c\ub3c4 \uc788\ub2e4. \uc11d\uc591 \uc18d\uc5d0\uc11c \ubd89\uac8c \ubb3c\ub4dc\ub294 \uc6b4\ud574, \uad6c\ub984 \uc704\ub97c \ub6f0\uc5b4\ub2e4\ub2c8\ub294 \ud558\ub298\ub3cc\uace0\ub798 \ub5bc. \uadf8\ub7f0 \uad11\uacbd\uc744 \ub208\uc5d0 \uc0c8\uae38 \ub54c, \ud0d0\uacf5\uc0ac\ub4e4\uc740 \uc790\uc2e0\uc774 \ud558\ub298 \uc704\ub97c \uc5ec\ud589\ud558\ub294 \uc758\ubbf8\ub97c \uae68\ub2eb\ub294\ub2e4.","\ud558\ub298\uace0\ub798\\n\uc120\ub0b4 \ub9f5\uc0c1\uc5d0 \uc788\ub294 \ubaa8\ub4e0 \uce90\ub9ad\ud130\ub294 \u3010\uc0dd\uba85\uc810\u3011\uc774 \u300c1D6\uc810\u300d \ud68c\ubcf5\ud55c\ub2e4.\\n \uad6c\ub984 \uc18d\uc5d0\uc11c \uac70\ub300\ud55c \ud558\ub298\uace0\ub798\uac00 \ub6f0\uc5b4\uc624\ub978\ub2e4. \ub028\uc6c5, \ud558\uace0 \ub3c5\ud2b9\ud55c \uc6b8\uc74c\uc18c\ub9ac\ub97c \ub0b4\uba70 \ucc9c\ucc9c\ud788 \ub099\ud558\ud558\ub4ef\uc774 \uad6c\ub984 \uc544\ub798\ub85c \ub3cc\uc544\uac00\ub294 \ud558\ub298\uace0\ub798\uc758 \uc6c5\ub300\ud55c \ubaa8\uc2b5\uc740 \uc27d\uac8c \ubcfc \uc218 \uc788\ub294 \uac83\uc774 \uc544\ub2c8\ub2e4. \uc228\uc744 \uc0bc\ud0a4\uac8c \ud558\ub294 \uc555\ub3c4\uc801\uc778 \uad11\uacbd\uc5d0 \uc7a0\uc2dc\ub098\ub9c8 \ub208\uae38\uc744 \ube7c\uc557\uae38 \uac83\ub9cc \uac19\ub2e4.","\uc5b4\uc7a5\\n\uc120\ub0b4 \ub9f5\uc0c1\uc5d0 \uc788\ub294 \ubaa8\ub4e0 \uce90\ub9ad\ud130\uc758 \u3010\uc0dd\uba85\uc810\u3011 \ucd5c\ub300\uce58\uac00 \u300c2\uc810\u300d \uc99d\uac00\ud55c\ub2e4. \uc774 \ud6a8\uacfc\ub294 \uc911\ubcf5\ub418\uc9c0 \uc54a\ub294\ub2e4.\\n\ube44\ud589 \uc624\uc9d5\uc5b4 \ub5bc\ub2e4! \uc544\ubb34\ub798\ub3c4 \ucc3d\uc73c\ub85c \uc0c8\uc5b4\ub098\uac04 \ube5b\uc5d0 \uc774\ub04c\ub824 \ubaa8\uc5ec\ub4e0 \uac83 \uac19\ub2e4. \uadf8\ubb3c\uc744 \uce58\uc790. \uc624\ub298 \ubc24\uc5d0\ub294 \uc2e0\uc120\ud55c \uc624\uc9d5\uc5b4\ub97c \uc794\ub729 \uba39\uc744 \uc218 \uc788\uc744 \uac83 \uac19\ub2e4.","\uae30\ub958+1\\n\ube44\uacf5\uc815 \ub9d0\uc744 1\uc2a4\ud3ff \uc804\uc9c4\uc2dc\ud0a8\ub2e4.\\n\ub2e4\ub978 \uacf5\uc5ed\ubcf4\ub2e4\ub3c4 \ube44\uad50\uc801 \uac15\ud55c \uae30\ub958\uac00 \uc77c\uc815\ud55c \ubc29\ud5a5\uc744 \ud5a5\ud574 \uacc4\uc18d\ud574\uc11c \ubd88\uace0 \uc788\ub294 \uacf5\uc5ed. \ub54c\ub85c\ub294 \ud0d0\uacf5\uc0ac\ub4e4\uc744 \ub3c4\uc640\uc8fc\uace0, \ub54c\ub85c\ub294 \ud0d0\uacf5\uc0ac\ub4e4\uc758 \uc55e\uae38\uc744 \uac00\ub85c\ub9c9\ub294\ub2e4. \uc5b4\ub290 \uacf5\uc5ed\uc5d0 \uc5b4\ub5a4 \ubc14\ub78c\uc774 \ubd80\ub294\uc9c0 \ud30c\uc545\ud574\uc11c \ub098\uc544\uac00\ub294 \uac83\uc774 \ud558\ub298 \uc5ec\ud589\uc758 \uae30\ubcf8\uc774\ub2e4.","\ucf00\uc138\ub791 \ud30c\uc0ac\ub791\\n\ub2f9\uc2e0\uc740 \uc120\ub0b4 \ub9f5\uc0c1\uc5d0 \ubc30\uce58\ub41c \uc784\uc758\uc758 \ud30c\uce20 1\uac1c\ub97c \uc120\ud0dd\ud55c\ub2e4. \uadf8 \ud30c\uce20\uc5d0 \u3010\ucf8c\uc801\ub3c4\u3011 \ud0a4\uc6cc\ub4dc \ud6a8\uacfc\uac00 \uc788\uc744 \uacbd\uc6b0, \uc774 \uc138\uc158 \ub3d9\uc548 \uadf8 \uc218\uce58\uac00 \u300c1\uc810\u300d \ub192\uc740 \uac83\uc73c\ub85c \uac04\uc8fc\ud55c\ub2e4.\\n\ud06c\uace0 \ud3ed\uc2e0\ud3ed\uc2e0\ud55c \uc19c\ud138 \uac19\uc740 \ubd80\uc720\uc0dd\ubb3c. \uadf8 \ud138\uc740 \uc0c1\ub958 \uacc4\uae09\uc758 \uc18c\ud30c\uc5d0\ub3c4 \uc0ac\uc6a9\ub420 \uc815\ub3c4\ub85c \ubd80\ub4dc\ub7ec\uc6b4 \uac10\ucd09\uc744 \uc790\ub791\ud55c\ub2e4. \uc7a1\uc544\uc11c \uae30\ub97c \uc218\ub9cc \uc788\ub2e4\uba74 \ud55c\ub54c\uc758 \ud790\ub9c1 \ud0c0\uc784\uc744 \uc81c\uacf5\ud574\uc904 \uac83\uc774\ub2e4.","\ud1a0\ub07c\ucf65\ud130\\n\ub2f9\uc2e0\uc758 \u3010\uc0dd\uba85\uc810\u3011\uc744 \u300c3\uc810\u300d \ud68c\ubcf5\ud55c\ub2e4.\\n\ub450 \uadc0\ub97c \ud68c\uc804\uc2dc\ucf1c \ud55c\ub974\uc744 \ub098\ub294 \ud1a0\ub07c\ub85c\ubc16\uc5d0 \ubcf4\uc774\uc9c0 \uc54a\ub294 \ubd80\uc720\uc0dd\ubb3c\uc774 \uacc4\uc18d \uc774\ucabd\uc744 \ubc14\ub77c\ubcf4\uace0 \uc788\ub2e4. \uadc0\uc5fd\uae34 \ud558\uc9c0\ub9cc, \uc774 \ub140\uc11d \ub3c4\ub300\uccb4 \ubb50\uc9c0\u2026\u2026?"]},"NavigationEventOnBoardTable":{"name":"\ud56d\ud589\uc774\ubca4\ud2b8\ud45c \uc120\ub0b4\uacc4","type":"2D6","items":["\uc219\ucde8\\n\ubc30\ub4dc \uc2a4\ud14c\uc774\ud130\uc2a4. \ub2f9\uc2e0\uc740 \ub2e4\uc74c \ud018\uc2a4\ud2b8 \ud398\uc774\uc988\ub97c \uc885\ub8cc\ud560 \ub54c\uae4c\uc9c0 \ubaa8\ub4e0 \ud589\ub3d9 \ud310\uc815\uc758 \u3010\ud38c\ube14\uce58\u3011\uac00 \u300c1\u300d \ub192\uc740 \uac83\uc73c\ub85c \uac04\uc8fc\ud55c\ub2e4.\\n\ub3d9\ub8cc\uc640\uc758 \uc990\uac70\uc6b4 \uc2dc\uac04, \ucc98\uc74c \ubcf4\ub294 \uadfc\uc0ac\ud55c \uacbd\uce58. \ud558\ub298 \uc704\uc758 \uc5ec\ub85c\uc5d0\ub294 \uc220\ub9db\uc744 \ub2f9\uae30\uac8c \ud558\ub294 \uac83\ub4e4\uc774 \ub118\uccd0\ub09c\ub2e4. \uadf8\ub7ec\ub2c8 \uc774\uac83\uc740 \ud558\ub298 \uc704\ub97c \uc5ec\ud589\ud560 \ub54c \ub9c8\uc8fc\uce58\uac8c \ub418\ub294 \ub2e4\uc591\ud55c \uc704\ud5d8 \uc911 \ud558\ub098\ub77c\uace0 \ud574\ub3c4 \uacfc\uc5b8\uc774 \uc544\ub2c8\ub2e4. \ud3ed\ud48d\uc774\ub098 \uacf5\uc801\ucc98\ub7fc \ub9d0\uc774\ub2e4. \uace8\uce58 \uc544\ud508 \uc874\uc7ac\ub2e4.","\uc774\ubbf8\uc9c0 \ub2e4\uc6b4\\n\uc120\ub0b4 \ub9f5\uc0c1\uc5d0 \uc788\ub294 \ubaa8\ub4e0 \uce90\ub9ad\ud130\ub294 \ub2f9\uc2e0\uc5d0 \ub300\ud574 \ud68d\ub4dd\ud55c \u3010\uc720\ub300\u3011\uc758 \u300c\uc9c0\uc6d0 \uccb4\ud06c\u300d\ub97c \uccb4\ud06c\ud55c\ub2e4.\\n\ub108, \uc815\ub9d0\ub85c \ubb34\uc2a8 \uc9d3\uc744 \ud55c \uac70\uc57c\u2026\u2026?","\ubc31\uc5f4\\n\ub2f9\uc2e0\uc740 \uc120\ub0b4 \ub9f5\uc0c1\uc5d0 \uc788\ub294 \uc784\uc758\uc758 \uce90\ub9ad\ud130 1\uba85\uc744 \uc120\ud0dd\ud55c\ub2e4. \uadf8 \ud6c4, \ub2f9\uc2e0\uc740 \uc120\ub0b4 \ub9f5\uc0c1\uc5d0\uc11c \uce90\ub9ad\ud130\uac00 \uc5c6\uace0 \ubc14\ub2e5 \uce78\uc774 2\uac1c \uc774\uc0c1\uc778 \ud30c\uce20\ub97c \uc120\ud0dd\ud55c\ub2e4. \uadf8 \ud30c\uce20\uc5d0\uc11c \uce90\ub9ad\ud130\uac00 \uc5c6\ub294 \ubc14\ub2e5 \uce78 2\uac1c\ub97c \uc784\uc758\ub85c \uace8\ub77c \ub2f9\uc2e0\uacfc \uc120\ud0dd\ud55c \uce90\ub9ad\ud130\ub97c \uc774\ub3d9\uc2dc\ud0a8\ub2e4. \uc774 \uc7a5\uba74 \ub3d9\uc548 \ub2f9\uc2e0\uc740 \u300c\uad50\ub958\u300d \uc774\uc678\uc758 \uc561\uc158\uc744 \ud560 \uc218 \uc5c6\ub2e4. (\ud30c\uce20\ub97c \uc120\ud0dd\ud560 \uc218 \uc5c6\ub2e4\uba74 \uce90\ub9ad\ud130\uc758 \uc774\ub3d9\uc740 \ubc1c\uc0dd\ud558\uc9c0 \uc54a\uc73c\uba70, \ub2f9\uc2e0\uc740 \uc774 \uc7a5\uba74 \ub3d9\uc548 \uc561\uc158\uc744 \ud560 \uc218 \uc5c6\ub2e4.)\\n\uac00\ubcbc\uc6b4 \uc2ec\uc2ec\ud480\uc774\uc77c \uc608\uc815\uc774\uc5c8\ub2e4. \ub2f9\uc2e0\uc774 \uce74\ub4dc\ub97c \ubf51\uc558\uace0, \uc0c1\ub300\ub3c4 \uac70\uae30\uc5d0 \uc751\ud588\uc744 \ubfd0\uc774\ub2e4. \ud558\uc9c0\ub9cc \uc774\uae34 \ucabd\uc740 \uae30\ubed0\ud558\uace0, \uc9c4 \ucabd\uc740 \ubd84\ud558\uac8c \uc5ec\uae30\ub294 \ubc95\uc774\ub2e4. \uadf8 \uac04\ub2e8\ud55c \uc9c4\uc2e4\uc744 \uc78a\uc5b4\uc11c\ub294 \uc548 \ub418\uc5c8\ub294\ub370.","\ubcf4\uace0 \ub9d0\uc558\ub2e4\\n\ub2f9\uc2e0\uc740 \uc790\uc2e0\uc774 \ud68d\ub4dd\ud55c \uc784\uc758\uc758 \u3010\uc720\ub300\u3011 \ud558\ub098\ub97c \uc120\ud0dd\ud55c\ub2e4. \uadf8 \u3010\uc720\ub300\u3011\ub97c \uc783\ub294\ub2e4.\\n\ub204\uad6c\uc5d0\uac8c\ub098 \uc228\uae30\uace0 \uc2f6\uc740 \ube44\ubc00 \ud55c\ub450 \uac00\uc9c0\ub294 \uc788\ub294 \ubc95\uc774\ub2e4. \uadf8\uac83\uc744 \ucc45\ub9dd\ud558\ub294 \uac83\uc740 \uc548 \ub420 \uc77c\uc774\ub2e4. \uc6b0\uc5f0\ud788 \uadf8 \uad11\uacbd\uc744 \ubaa9\uaca9\ud55c \uac83\uc740 \ub2f9\uc2e0 \uc798\ubabb\uc774 \uc544\ub2c8\ub2e4. \uadf8\ub0e5 \uc6b4\uc774 \ub098\ube74\uc744 \ubfd0\uc774\ub2e4. \uadf8\ub807\uc9c0?","\uc2f8\uc6c0\\n\ub2f9\uc2e0\uc740 \uc790\uc2e0\uc774 \ud68d\ub4dd\ud55c \uc784\uc758\uc758 \u3010\uc720\ub300\u3011 \ud558\ub098\ub97c \uc120\ud0dd\ud558\uace0, \uadf8 \u300c\uc9c0\uc6d0 \uccb4\ud06c\u300d\ub97c \uccb4\ud06c\ud55c\ub2e4. \ub9cc\uc57d \ub2f9\uc2e0\uc774 \ud68d\ub4dd\ud55c \ubaa8\ub4e0 \u3010\uc720\ub300\u3011\uc758 \u300c\uc9c0\uc6d0 \uccb4\ud06c\u300d\uac00 \uccb4\ud06c\ub418\uc5b4 \uc788\ub2e4\uba74, \uc55e\uc5d0\uc11c \uc5b8\uae09\ud55c \uccb4\ud06c \ucc98\ub9ac\ub97c \ud558\ub294 \ub300\uc2e0\uc5d0 \ub2f9\uc2e0\uc758 \u3010\uc0dd\uba85\uc810\u3011\uc774 \u300c1D6\uc810\u300d \uac10\uc18c\ud55c\ub2e4.\\n\uc0ac\uc18c\ud55c \ub9d0\ub2e4\ud23c\uc73c\ub85c \ud300\uc6cc\ud06c\uc5d0 \uae08\uc774 \uac00\ub294 \uc77c\uc740 \uace7\uc798 \uc77c\uc5b4\ub09c\ub2e4. \uc778\uac04\uad00\uacc4\ub780 \ub54c\ub54c\ub85c \uc778\uc0dd\uc5d0 \uadf8\ub9bc\uc790\ub97c \ub4dc\ub9ac\uc6b4\ub2e4. \uadf8\ub798\ub3c4 \uc2f8\uc6b8 \uc0c1\ub300\uc870\ucc28 \uc5c6\ub294 \uac83\ubcf4\ub2e4\uc57c \ud6e8\uc52c \ub098\uc740 \uc778\uc0dd\uc774\ub77c\uace0 \ubcf4\uc9c0\ub9cc \ub9d0\uc774\ub2e4.","\uc218\ub2e4\\n\uc774 \uc7a5\uba74 \ub3d9\uc548 \ub2f9\uc2e0\uc774 \u300c\uad50\ub958\u300d \uc561\uc158\uc744 \ud560 \ub54c, \ub2f9\uc2e0\uc774 \uc788\ub294 \ud30c\uce20 \ub0b4\uc758 \uce90\ub9ad\ud130\uac00 \uc544\ub2cc \ub2e4\ub978 \uce90\ub9ad\ud130\ub3c4 \uc120\ud0dd\ud560 \uc218 \uc788\ub2e4.\\n\uc120\ub0b4 \uacf3\uacf3\uc73c\ub85c \ubed7\uc5b4 \uc788\ub294 \uc804\uc131\uad00\uc744 \uc774\uc6a9\ud558\uba74 \uc120\ub0b4\uc758 \ub204\uad6c\uc640\ub3c4 \uc5f0\ub77d\uc744 \ucde8\ud560 \uc218 \uc788\ub2e4. \uc720\uc0ac\uc2dc\ub97c \uc704\ud55c \uc124\ube44\uc9c0\ub9cc, \ud558\uc798 \uac83 \uc5c6\ub294 \uc218\ub2e4\ub85c \uc774\uc57c\uae30\uaf43\uc744 \ud53c\uc6b0\ub294 \uac83\ub3c4 \uad1c\ucc2e\uaca0\uc9c0.","\ub2ec\uc774 \uc544\ub984\ub2f5\uad70\uc694\\n\ub2f9\uc2e0\uc740 \uc120\ub0b4 \ub9f5\uc0c1\uc5d0\uc11c \uc784\uc758\uc758 \uce90\ub9ad\ud130 1\uba85\uc744 \uc120\ud0dd\ud55c\ub2e4. \ub2f9\uc2e0\uc740 \uadf8 \uce90\ub9ad\ud130\uc5d0 \ub300\ud55c \u3010\uc720\ub300\u3011\ub97c \ud68d\ub4dd\ud55c\ub2e4.\\n\ubc24\ud558\ub298\uc5d0 \ub72c \ub178\ub780 \ub2ec\uc774, \ub465\uadfc \ub2ec\uc774 \ube5b\ub098\uace0 \uc788\ub2e4. \ud56d\uc0c1 \uc5d4\uc9c4 \uc18c\ub9ac\ub85c \uc2dc\ub044\ub7ec\uc6b4 \ubc30 \uc548\uc774\uc9c0\ub9cc, \ub2ec\uc744 \ubcfc \ub54c\ub9cc\ud07c\uc740 \uc660\uc9c0 \uace0\uc694\ud558\uac8c \ub290\uaef4\uc9c4\ub2e4.","\uc120\ubb3c\\n\ub2f9\uc2e0\uc740 \uc120\ub0b4 \ub9f5\uc0c1\uc5d0\uc11c \uc784\uc758\uc758 \uce90\ub9ad\ud130 1\uba85\uc744 \uc120\ud0dd\ud55c\ub2e4. \uadf8 \uce90\ub9ad\ud130\ub294 \ub2f9\uc2e0\uc5d0 \ub300\ud55c \u3010\uc720\ub300\u3011\ub97c \ud68d\ub4dd\ud55c\ub2e4.\\n\uc870\uae08 \ud638\ud654\ub85c\uc6b4 \ub290\ub08c\uc758 \ud3ec\uc7a5\uc9c0\ub85c \uc2fc \ucd08\ucf5c\ub9bf, \ud3c9\uc18c\uc5d0\ub294 \uc798 \ub9c8\uc2dc\uc9c0 \uc54a\ub294 \uba85\ud488 \ube0c\ub79c\ub4dc\uc758 \ucc3b\uc78e, \ub531\ud788 \ud070 \uc758\ubbf8\uac00 \uc788\ub294 \uac83\uc740 \uc544\ub2cc \uc7a5\uc2dd\ubb3c. \ud589\ubcf5\uc774\ub780 \uacb0\uad6d \uadf8\ub7f0 \ubb3c\uac74\ub4e4\uacfc \ub204\uad70\uac00\uc758 \ub9c8\uc74c\uc73c\ub85c \uc774\ub8e8\uc5b4\uc838 \uc788\ub294 \ubc95\uc774\ub2e4.","\uae30\ub958+1\\n\ube44\uacf5\uc815 \ub9d0\uc744 1\uc2a4\ud3ff \uc804\uc9c4\uc2dc\ud0a8\ub2e4.\\n\ub2e4\ub978 \uacf5\uc5ed\ubcf4\ub2e4\ub3c4 \ube44\uad50\uc801 \uac15\ud55c \uae30\ub958\uac00 \uc77c\uc815\ud55c \ubc29\ud5a5\uc744 \ud5a5\ud574 \uacc4\uc18d\ud574\uc11c \ubd88\uace0 \uc788\ub294 \uacf5\uc5ed. \ub54c\ub85c\ub294 \ud0d0\uacf5\uc0ac\ub4e4\uc744 \ub3c4\uc640\uc8fc\uace0, \ub54c\ub85c\ub294 \ud0d0\uacf5\uc0ac\ub4e4\uc758 \uc55e\uae38\uc744 \uac00\ub85c\ub9c9\ub294\ub2e4. \uc5b4\ub290 \uacf5\uc5ed\uc5d0 \uc5b4\ub5a4 \ubc14\ub78c\uc774 \ubd80\ub294\uc9c0 \ud30c\uc545\ud574\uc11c \ub098\uc544\uac00\ub294 \uac83\uc774 \ud558\ub298 \uc5ec\ud589\uc758 \uae30\ubcf8\uc774\ub2e4.","\uae30\ub150\uc77c\\n\uc120\ub0b4 \ub9f5\uc0c1\uc5d0 \uc788\ub294 \ubaa8\ub4e0 \uce90\ub9ad\ud130\ub294 \ub2f9\uc2e0\uc5d0 \ub300\ud574 \u3010\uc720\ub300\u3011\ub97c \ud68d\ub4dd\ud55c\ub2e4.\\n\uc624\ub298 \uc544\uce68\uc5d0 \uc77c\uc5b4\ub0ac\uc744 \ub54c\ubd80\ud130 \ub2e4\ub4e4 \ubb18\ud558\uac8c \uc300\uc300\ub9de\ub2e4. \ub9d0\uc744 \uac78\uc5b4\ub3c4 \uc694\ub839\ubd80\ub4dd\uc774\uace0, \ub098\ub9cc \ub530\ub3cc\ub9ac\uace0 \ubb54\uac00 \uc774\uc57c\uae30\ub97c \ub098\ub204\ub294 \uac83 \uac19\ub2e4. \uc774\ub7f0 \ubd84\uc704\uae30 \ubcc4\ub860\ub370. \ub098 \ubb54\uac00 \uc800\uc9c8\ub800\ub098? \uc5b4, \uc7a0\uae50. \uadf8\ub7ec\uace0 \ubcf4\uba74 \uc624\ub298\uc740\u2026\u2026.","\ud30c\ud2f0\\n\uc120\ub0b4 \ub9f5\uc0c1\uc5d0 \uc788\ub294 \ubaa8\ub4e0 \uce90\ub9ad\ud130\ub294 \uac01\uc790 \uc784\uc758\uc758 \uce90\ub9ad\ud130 1\uba85\uc744 \uc120\ud0dd\ud55c\ub2e4. \uac01 \uce90\ub9ad\ud130\ub294 \uc120\ud0dd\ud55c \uce90\ub9ad\ud130\uc5d0 \ub300\ud55c \u3010\uc720\ub300\u3011\ub97c \ud68d\ub4dd\ud55c\ub2e4.\\n\uc624\ub298\ub9cc\ud07c\uc740 \uc77c \ub530\uc704 \uc78a\uc5b4\ubc84\ub9ac\uc790! \ud55c\ud1b5 \uac00\ub4dd \ub2f4\uc740 \ub9e5\uc8fc\uc5d0, \uc120\uc7a5\uc774 \uc900\ube44\ud55c \ube44\uc7a5\uc758 \uc640\uc778. \ud14c\uc774\ube14 \uc704\uc5d0\ub294 \uc5ec\ud558\ud2bc \ub9c8\uc2e4 \uac83\uacfc \uc548\uc8fc\uac00 \uac00\ub4dd \ucc28\ub824\uc838 \uc788\ub2e4."]},"NavigationEventHardTable":{"name":"\ud56d\ud589\uc774\ubca4\ud2b8\ud45c \uace4\ub780\uacc4","type":"2D6","items":["\ubd80\uc0c1\\n\ubc30\ub4dc \uc2a4\ud14c\uc774\ud130\uc2a4. \ub2f9\uc2e0\uc740 \ub2e4\uc74c \ud018\uc2a4\ud2b8 \ud398\uc774\uc988\ub97c \uc885\ub8cc\ud560 \ub54c\uae4c\uc9c0 \u3010\uc0dd\uba85\uc810\u3011 \ucd5c\ub300\uce58\uac00 \u300c5\uc810\u300d\uc774 \ub41c\ub2e4. \u3010\uc0dd\uba85\uc810\u3011\uc774 5\uc810 \uc774\uc0c1\uc774\ub77c\uba74, \ub354\ubd88\uc5b4 \u3010\uc0dd\uba85\uc810\u3011\uc774 \u300c5\uc810\u300d\uc774 \ub41c\ub2e4.\\n\uc880 \ub2e4\ucce4\ub2e4\uace0 \ud574\uc11c \uc5ec\ud589\uc744 \uadf8\ub9cc\ub458 \uc774\uc720\ub294 \uc5c6\ub2e4. \ud558\uc9c0\ub9cc \ubb58 \ud558\ub824\uace0 \ud574\ub3c4 \ub2f9\uc2e0\uc758 \ubab8\uc744 \ub36e\uce58\ub294 \uaca9\ud1b5\uc740 \ud655\uc2e4\ud558\uac8c \ub2f9\uc2e0\uc744 \ubc29\ud574\ud560 \ud14c\uc9c0. \uc774\uc81c\uae4c\uc9c0\uc640 \uac19\uc774 \ub9c8\uc74c \ud3b8\ud788 \uc5ec\ud589\ud558\uc9c0\ub294 \ubabb\ud560 \uac83\uc774\ub2e4.","\ub300\uc7ac\ud574\\n\ube44\uacf5\uc815\uc740 \u300cD/5\u300d\uc758 \ub300\ubbf8\uc9c0\ub97c \uc785\ub294\ub2e4. \uc774 \ub300\ubbf8\uc9c0\uc5d0\ub294 \u300a\ud68c\ud53c\uc6b4\ub3d9\u300b \uc2a4\ud0ac \ud6a8\uacfc\ub97c \uc0ac\uc6a9\ud560 \uc218 \uc5c6\ub2e4.\\n\ud558\ub298 \uc704\uc5d0\uc11c \uc5ec\ud589\uc744 \uacc4\uc18d\ud558\ub2e4 \ubcf4\uba74 \uc5b4\uca54 \ub3c4\ub9ac\uac00 \uc5c6\ub294 \uc0c1\ud669\uacfc \ub9de\ub2e5\ub728\ub9ac\ub294 \uac83\uc740 \uc77c\uc0c1\ub2e4\ubc18\uc0ac\ub2e4. \uadf8\ub7f4 \ub54c\ub294 \uc5b4\ub5bb\uac8c \ud574\uc57c \ud558\ub290\ub0d0\uace0? \uc190\uc4f8 \ubc29\ubc95\uc774 \uc5c6\uc73c\ub2c8 \uae30\ub3c4\ub77c\ub3c4 \ud560\uae4c? \ubed4\ud558\uc796\uc544. \uadf8\ub798\ub3c4 \uc5b4\ub5bb\uac8c\ub4e0 \ud558\ub294 \uac70\ub77c\uace0.","\uc790\uc5f0\uc758 \ub9f9\uc704\\n\ube44\uacf5\uc815\uc740 \u300cD/4\u300d\uc758 \ub300\ubbf8\uc9c0\ub97c \uc785\ub294\ub2e4. \uc774 \ub300\ubbf8\uc9c0\uc5d0\ub294 \u300a\ud68c\ud53c\uc6b4\ub3d9\u300b \uc2a4\ud0ac \ud6a8\uacfc\ub97c \uc0ac\uc6a9\ud560 \uc218 \uc5c6\ub2e4.\\n\u300e\ud0d1\u300f \uc704\uc5d0 \uc0ac\ub294 \uc778\uac04\uc740 \uc5b8\uc820\uac00 \uc778\uac04\uc774 \uc790\uc5f0\uc744 \uc9c0\ubc30\ud560 \ub54c\uac00 \uc624\ub9ac\ub77c\uace0 \ubbff\ub294\ub2e4. \uc790\uc5f0\uc758 \ud798\uc744 \ubab8\uc73c\ub85c \uccb4\uac10\ud558\ub294 \ud0d0\uacf5\uc0ac \uc911\uc5d0 \uadf8\ub7f0 \uc0dd\uac01\uc744 \ud558\ub294 \ub140\uc11d\uc740 \uc5c6\ub2e4.","\uc545\ucc9c\ud6c4\\n\ub2e4\uc74c \uc7a5\uba74\uc5d0\uc11c\ub294 \uc7a5\uba74 \ud50c\ub808\uc774\uc5b4\uac00 \ud56d\ud589 \uccb4\ud06c\ub97c \ud560 \uc218 \uc5c6\uc73c\uba70, \u300c\uc870\ud0c0\uc5d0 \uc804\ub150\ud558\uae30\u300d\ub97c \uc120\uc5b8\ud560 \uc218\ub3c4 \uc5c6\ub2e4.\\n\ub611, \ub611, \ud558\uace0 \ucc3d\uc5d0 \ube57\ubc29\uc6b8\uc774 \ub5a8\uc5b4\uc9c4\ub2e4. \uc774\ub807\uac8c \ub418\uba74 \ubc30\ub97c \ubab0 \uc218 \uc5c6\ub2e4. \uc774 \ud2c8\uc5d0 \ud64d\ucc28\ub77c\ub3c4 \ud55c \uc794 \ud558\ub294 \uac83\ub3c4 \uad1c\ucc2e\uaca0\uc9c0. \ud2f0\ud0c0\uc784\uc5d0\ub294 \ucd5c\uc545\uc778 \ub0a0\uc528\uc9c0\ub9cc.","\ub099\ub8b0\\n\ube44\uacf5\uc815\uc740 \u300cD/3\u300d\uc758 \ub300\ubbf8\uc9c0\ub97c \uc785\ub294\ub2e4. \uc774 \ub300\ubbf8\uc9c0\uc5d0\ub294 \u300a\ud68c\ud53c\uc6b4\ub3d9\u300b \uc2a4\ud0ac \ud6a8\uacfc\ub97c \uc0ac\uc6a9\ud560 \uc218 \uc5c6\ub2e4.\\n\uac11\uc790\uae30 \uad49\uc74c\uacfc \uc12c\uad11\uc774 \ube44\uacf5\uc815\uc744 \uac10\uc2fc\ub2e4. \ub2f9\uc2e0\uc774 \ubb34\uc2a8 \uc77c\uc774 \uc77c\uc5b4\ub0ac\ub294\uc9c0 \ud655\uc778\ud558\uae30\ub3c4 \uc804\uc5d0 \ubc30\uc5d0 \ubcbc\ub77d\uc774 \ud280\uace0, \uc5b4\ub518\uac00\uac00 \uadf8\uc744\ub824 \ub9e4\uce90\ud55c \ub0c4\uc0c8\uac00 \ucf54\ub97c \ucc0c\ub978\ub2e4. \uc774\uac74 \uc704\ud5d8\ud558\ub2e4. \ube68\ub9ac \uc774 \uacf5\uc5ed\uc5d0\uc11c \ubc97\uc5b4\ub098\uc57c \ud574!","\uac8c\ub9b4\ub77c\uc131 \ub1cc\uc6b0\\n\ube44\uacf5\uc815\uc740 \u300cD/3\u300d\uc758 \ub300\ubbf8\uc9c0\ub97c \uc785\ub294\ub2e4.\\n\ud3ed\ud48d\uc5d0 \ud718\ub9d0\ub9ac\uc9c0 \uc54a\ub3c4\ub85d \ucd5c\ub300 \ucd9c\ub825\uc73c\ub85c \ube44\ubc14\ub78c\uc744 \uac00\ub978\ub2e4. \uc5d4\uc9c4\uc740 \ubd89\uac8c \ub2ec\uc544\uc624\ub974\uace0, \ubfdc\uc5b4 \uc62c\ub77c\uc624\ub294 \uc99d\uae30\uac00 \uae30\uad00\uc2e4\uc744 \ud558\uc597\uac8c \ucc44\uc6b4\ub2e4. \uc81c\ubc1c \uc880 \ubc84\ud168\ub2e4\uc624. \uc774\ub7f0 \uacf3\uc5d0\uc11c \ud558\ub298\uc5d0 \ub0b4\ub3d9\ub315\uc774\uccd0\uc9c0\ub294 \uac74 \uc0ac\uc591\uc774\uc57c.","\ud3ed\ud48d\uc758 \uc804\uc870\\n\ub2e4\uc74c \uc7a5\uba74\uc5d0\uc11c\ub294 \uc2a4\ud3ff\uc758 \uc774\ubca4\ud2b8 \ud0c0\uc785\uc774\ub098 \u300c\uadf8 \uc790\ub9ac\uc5d0 \uba38\ubb34\ub974\uae30\u300d \uc120\uc5b8 \uc5ec\ubd80\uc640 \uad00\uacc4\uc5c6\uc774 \u300c\ud56d\ud589 \uc774\ubca4\ud2b8 \ud45c: \uace4\ub780\uacc4\u300d\ub97c \uc0ac\uc6a9\ud574\uc11c \ud56d\ud589 \uc774\ubca4\ud2b8\ub97c \uacb0\uc815\ud55c\ub2e4.\\n\uac11\uc790\uae30 \ub208\uc55e\uc5d0 \ub098\ud0c0\ub09c \uc2dc\ucee4\uba3c \uad6c\ub984. \uae30\uc555\uacc4\uc758 \ubc14\ub298\uc774 \ube60\ub978 \uc18d\ub3c4\ub85c \ub0b4\ub824\uac00\uba70 \uc5ec\ub7ec\ubd84\uc5d0\uac8c \ubd88\uc628\ud55c \uc0ac\ud0dc\uc758 \uc9d5\uc870\ub97c \uc54c\ub9b0\ub2e4. \uc774\uac70 \uc544\ubb34\ub798\ub3c4 \uac01\uc624\ub97c \ud574\ub46c\uc57c \ud560 \uac83 \uac19\ub2e4\u2026\u2026.","\uc545\ucc9c\ud6c4\\n\ub2e4\uc74c \uc7a5\uba74\uc5d0\uc11c\ub294 \uc7a5\uba74 \ud50c\ub808\uc774\uc5b4\uac00 \ud56d\ud589 \uccb4\ud06c\ub97c \ud560 \uc218 \uc5c6\uc73c\uba70, \u300c\uc870\ud0c0\uc5d0 \uc804\ub150\ud558\uae30\u300d\ub97c \uc120\uc5b8\ud560 \uc218\ub3c4 \uc5c6\ub2e4.\\n\ub611, \ub611, \ud558\uace0 \ucc3d\uc5d0 \ube57\ubc29\uc6b8\uc774 \ub5a8\uc5b4\uc9c4\ub2e4. \uc774\ub807\uac8c \ub418\uba74 \ubc30\ub97c \ubab0 \uc218 \uc5c6\ub2e4. \uc774 \ud2c8\uc5d0 \ud64d\ucc28\ub77c\ub3c4 \ud55c \uc794 \ud558\ub294 \uac83\ub3c4 \uad1c\ucc2e\uaca0\uc9c0. \ud2f0\ud0c0\uc784\uc5d0\ub294 \ucd5c\uc545\uc778 \ub0a0\uc528\uc9c0\ub9cc.","\uae30\ub958+2\\n\ube44\uacf5\uc815 \ub9d0\uc744 2\uc2a4\ud3ff \uc804\uc9c4\uc2dc\ud0a8\ub2e4.\\n\uc774 \uadfc\ucc98\ub294 \uc704\ud5d8\ud55c \uacf5\uc5ed\uc778 \ubaa8\uc591\uc774\uc9c0\ub9cc, \uc774 \uc815\ub3c4\uc758 \ubc14\ub78c\uc5d0 \ub300\uc751\ud558\uc9c0 \ubabb\ud55c\ub2e4\uba74 \ud504\ub85c\ub77c\uace0 \ud560 \uc218 \uc5c6\ub2e4. \uc2e4\uc81c\ub85c \ub2f9\uc2e0\uc740 \ud6cc\ub96d\ud558\uac8c \ud574\ub0c8\ub2e4. \ud0d0\uacf5\uc0ac\uc758 \uc2e4\ub825\uc774\ub780 \ubc14\ub85c \uc774\ub7f0 \uac70\ub2e4.","\uac70\uce5c \ud558\ub298\\n\ub2f9\uc2e0\uc740 \u300c\uc2e0\uccb4/\uac10\uac01: 7\u300d\uc758 \ud589\ub3d9 \ud310\uc815\uc744 3\ud68c \ud55c\ub2e4. \uc2e4\ud328\ud55c \ud69f\uc218\ub9cc\ud07c \ube44\uacf5\uc815\uc774 \u300cD/3\u300d\uc758 \ub300\ubbf8\uc9c0\ub97c \uc785\ub294\ub2e4.\\n\uba4d\ud558\ub2c8 \uc788\uae34 \ud588\uc9c0\ub9cc, \uc774\uac74 \ub108\ubb34\ud558\uc796\uc544! \uc815\uc2e0\uc744 \ucc28\ub9ac\uace0 \ubcf4\ub2c8 \ubc30\ub294 \ud3ed\ud48d \uc9c0\ub300 \ud55c\ubcf5\ud310. \uc5ec\uae30\ub97c \ube60\uc838\ub098\uac00\ub294 \uac83\uc740 \ub2e8\uc21c\ud55c \ub09c\uae30\ub958\ub97c \ubc97\uc5b4\ub098\ub294 \uac83\uacfc\ub294 \ube44\uad50\ub3c4 \uc548 \ub418\uac8c \uc5b4\ub824\uc6b4 \uc77c\uc774\ub2e4.","\uc800\uae30\uc555\uc758 \uc911\uc2ec\\n\ub2f9\uc2e0\uc740 \u300c\uc2e0\uccb4/\uac10\uac01: 9\u300d\uc758 \ud589\ub3d9 \ud310\uc815\uc744 \ud55c\ub2e4. \uc774 \ud310\uc815\uc5d0 \uc131\uacf5\ud558\uc9c0 \ubabb\ud558\uba74 \ube44\uacf5\uc815 \ub9d0\uc740 \ud50c\ub77c\uc774\ud2b8 \uc5d0\uc774\ub9ac\uc5b4 \uc704\ub97c \uc774\ub3d9\ud560 \uc218 \uc5c6\uc73c\uba70, \ub2e4\uc74c \uc7a5\uba74\uc758 \ud56d\ud589 \uc774\ubca4\ud2b8\ub3c4 \uc790\ub3d9\uc73c\ub85c \u300c\uc800\uae30\uc555\uc758 \uc911\uc2ec\u300d\uc774 \ub41c\ub2e4. \ud589\ub3d9 \ud310\uc815\uc758 \uc131\uacf5 \uc5ec\ubd80\uc640 \uad00\uacc4\uc5c6\uc774, \ube44\uacf5\uc815\uc740 \uadf8 \ud6c4 \u300cD/4\u300d\uc758 \ub300\ubbf8\uc9c0\ub97c \uc785\ub294\ub2e4. \uc774 \ub300\ubbf8\uc9c0\uc5d0\ub294 \u300a\ud68c\ud53c\uc6b4\ub3d9\u300b\uc758 \uc2a4\ud0ac \ud6a8\uacfc\ub97c \uc0ac\uc6a9\ud560 \uc218 \uc5c6\ub2e4.\\n\uac70\ub300\ud55c \uc18c\uc6a9\ub3cc\uc774\ub85c \ubcc0\ud55c \uad6c\ub984. \ub2e4\uac00\uac00\uae30\ub9cc \ud574\ub3c4 \ubc30\uac00 \ube68\ub824 \ub4e4\uc5b4\uac04\ub2e4. \ud55c\uc21c\uac04\uc774\ub77c\ub3c4 \ud0a4\ub97c \ub193\uc73c\uba74 \uc9d1\uc5b4 \uc0bc\ucf1c\uc9c0\uace0 \ub9d0 \uac83\uc774\ub2e4. \uadf8 \uc555\ub3c4\uc801\uc778 \ud798\uc744 \uc0c1\ub300\ud558\uace0 \uc788\ub178\ub77c\uba74 \ub9c8\uce58 \uac70\ub300\ud55c \uc6a9\uc5d0\uac8c \uc2b5\uaca9\ub2f9\ud558\ub294 \uae30\ubd84\uc774\ub2e4."]},"NavigationEventLongJourneyTable":{"name":"\ud56d\ud589\uc774\ubca4\ud2b8\ud45c \uc7a5\uac70\ub9ac \uc5ec\ud589\uacc4","type":"2D6","items":["\uc5f4\ubcd1\\n\ubc30\ub4dc \uc2a4\ud14c\uc774\ud130\uc2a4. \ub2f9\uc2e0\uc740 \ub2e4\uc74c \ud018\uc2a4\ud2b8 \ud398\uc774\uc988\ub97c \uc885\ub8cc\ud560 \ub54c\uae4c\uc9c0 \ubb34\uc5c7\uc774\ub4e0 \ud589\ub3d9 \ud310\uc815\uc744 \ud560 \ub54c\ub9c8\ub2e4 \u3010\uc0dd\uba85\uc810\u3011\uc774 \u300c1\uc810\u300d \uac10\uc18c\ud55c\ub2e4.\\n\uc774\ucbe4 \ub418\uba74 \uc624\ud788\ub824 \ub9c8\uc74c\uc774 \ub4e4\ub72c\ub2e4\ub2c8\uae4c. \uc9c0\uae08\uc774\ub77c\uba74 \ubb58 \ud574\ub3c4 \uc798 \ud480\ub9b4 \uac83 \uac19\uace0, \uc798 \ud480\ub9ac\uc9c0 \uc54a\uc544\ub3c4 \uac1c\uc758\uce58 \uc54a\uc744 \uac83 \uac19\uc544.","\uc5b4\uc0c9\ud55c \ubd84\uc704\uae30\\n\uc120\ub0b4 \ub9f5\uc0c1\uc758 \uce90\ub9ad\ud130 \uc804\uc6d0\uc740 \uc790\uc2e0\uc774 \ud68d\ub4dd\ud55c \ubaa8\ub4e0 \u3010\uc720\ub300\u3011\uc758 \u300c\uc9c0\uc6d0 \uccb4\ud06c\u300d\ub97c \uccb4\ud06c\ud574\uc57c \ud55c\ub2e4.\\n\ub204\uad70\uac00 \ud55c \uc0ac\ub78c\uc758 \ud0d3\uc774 \uc544\ub2c8\ub2e4. \uad73\uc774 \ub9d0\ud558\uc790\uba74 \ubaa8\ub450\uc758 \ud0d3\uc774\ub2e4. \ud558\uc9c0\ub9cc \ub204\uad70\uac00\uc5d0\uac8c \ucc45\uc784\uc744 \ub3cc\ub9ac\uc9c0 \uc54a\uc73c\uba74 \uacac\ub51c \uc218 \uc5c6\uc5c8\ub2e8 \ub9d0\uc774\uc57c.","\uc5d4\uc9c4 \ud2b8\ub7ec\ube14\\n\ub2f9\uc2e0\uc740 \uc120\ub0b4 \ub9f5\uc0c1\uc5d0\uc11c \uc784\uc758\ub85c \u300c\uae30\uad00\uacc4\u300d \ud30c\uce20\ub97c 1\uac1c \uace0\ub978\ub2e4. \uadf8\ub9ac\uace0 \uadf8 \ud30c\uce20 \ub0b4\uc5d0\uc11c \uc784\uc758\ub85c [\ud30c\uc190]\ub418\uc9c0 \uc54a\uc740 \uce78 1\uac1c\ub97c \uc120\ud0dd\ud55c\ub2e4. \uadf8 \uce78\uc740 [\ud30c\uc190]\ub41c\ub2e4.\\n\u2026\u2026\ud391!","\uc2dd\ub7c9\ub09c\\n\uc120\ub0b4 \ub9f5\uc0c1\uc758 \uce90\ub9ad\ud130 \uc804\uc6d0\uc740 \u3010\uc0dd\uba85\uc810\u3011\uc774 \u300c2\uc810\u300d \uac10\uc18c\ud55c\ub2e4.\\n\uc5ec\ub7ec\ubd84\uc758 \uc55e\uc5d0\ub294 \ub2ec\ub791 \ud1b5\uc870\ub9bc \ud558\ub098\ubfd0. \uc5ec\ub7ec\ubd84\uc740 \uc5bc\uad74\uc744 \ub9de\ub300\uace0 \uc11c\ub85c\ub97c \ub178\ub824\ubcf8\ub2e4. \uc5b4\ucc28\ud53c \uc774\ub7f0 \uac78 \ub193\uace0 \uc2f8\uc6cc\ubd10\uc57c \uc544\ubb34\ub7f0 \ud574\uacb0\ub3c4 \ub418\uc9c0 \uc54a\uc744 \ud150\ub370.","\ub178\ud6c4\ud654\\n\ub2f9\uc2e0\uc774 \uc788\ub294 \uce78\uc774 [\ud30c\uc190]\ub41c\ub2e4. \uc774 [\ud30c\uc190]\uc73c\ub85c\ub294 \ub2f9\uc2e0\uc758 \u3010\uc0dd\uba85\uc810\u3011\uc774 \uac10\uc18c\ud558\uc9c0 \uc54a\ub294\ub2e4.\\n\ucd5c\uadfc \ub07c\uac71\uac70\ub9ac\uae34 \ud588\uc9c0. \uc54c\uace0 \uc788\uae30\uc57c \ud588\uc5b4. \ub098\uc911\uc5d0 \uc218\ub9ac\ud574\uc57c\uaca0\ub2e4\uace0 \uc0dd\uac01\ud588\uc9c0. \ud558\uc9c0\ub9cc \uc774\ub807\uac8c \uac11\uc790\uae30 \uc640\uc791, \ud558\uace0 \ub9dd\uac00\uc9c8 \uc904\uc740 \ubab0\ub790\uc5b4. \uc9c4\uc9dc\ub77c\ub2c8\uae4c?","\uc544\uc9c1 \uad1c\ucc2e\uc544\u2026\\n\uc774 \ud56d\ud589 \uc774\ubca4\ud2b8\uc5d0\ub294 \uc544\ubb34\ub7f0 \ud6a8\uacfc\ub3c4 \uc5c6\ub2e4.\\n\uc9c0\uae08 \uc774 \uc2dc\uc810\uc5d0\uc11c, \uc774 \ubc30\ub294 \uc544\uc9c1 \uc548\uc804\ud558\uac8c \ud558\ub298 \uc704\ub97c \uc5ec\ud589\ud560 \ub9cc\ud07c\uc758 \uc5ec\uc720\uac00 \uc788\ub2e4. \ud558\uc9c0\ub9cc \uc5ec\ub7ec\ubd84\uc758 \ub1cc\ub9ac\uc5d0\ub294 \ubd88\uc548\uc774 \uce58\uc19f\ub294\ub2e4. \uc548\uc804\ud558\uae30\ub294 \ud574\ub3c4, \uc548\uc2ec\ud560 \uc218\ub294 \uc5c6\uae30 \ub54c\ubb38\uc774\ub2e4.","\uc55e\uae38\uc744 \uc11c\ub450\ub974\ub2e4\\n\uc774 \uc7a5\uba74 \ub3d9\uc548 \ub2f9\uc2e0\uc740 \uc561\uc158\uc744 \ud560 \uc218 \uc5c6\ub2e4. \uadf8 \ub300\uc2e0 \ube44\uacf5\uc815 \ub9d0\uc744 1\uc2a4\ud3ff \uc774\ub3d9\uc2dc\ud0a8\ub2e4.\\n\uc11c\ub450\ub974\uc790. \ucd08\uc870\ud568\uc5d0 \uc2dc\ub2ec\ub9ac\ub294 \uac00\uc6b4\ub370, \uc5ec\ub7ec\ubd84\uc740 \ub108\ub098 \ud560 \uac83 \uc5c6\uc774 \uadf8\ub807\uac8c \uc0dd\uac01\ud588\ub2e4. \ub290\uae0b\ud558\uac8c \uc5ec\ud589\uc744 \uc990\uae38 \ub54c\uac00 \uc544\ub2c8\ub2e4. \uadf8\ub7f0 \uc2dc\uae30\ub294 \uc774\ubbf8 \ud55c\ucc38 \uc804\uc5d0 \uc9c0\ub098\uac14\ub2e4.","\ud32c\ub354\ubbf9\\n\ub2f9\uc2e0\uc740 \u300c\ubc30\uba40\ubbf8\u300d, \u300c\uac10\uae30\u300d, \u300c\uc219\ucde8\u300d, \u300c\ubd80\uc0c1\u300d \uc911 \ud558\ub098\ub97c \uc120\ud0dd\ud55c\ub2e4. \uc120\ub0b4 \ub9f5\uc0c1\uc5d0 \uc788\ub294 \uce90\ub9ad\ud130 \uc804\uc6d0\uc740 \uc120\ud0dd\ub41c \ud56d\ud589 \uc774\ubca4\ud2b8\uc5d0 \uae30\uc7ac\ub41c \ubc30\ub4dc \uc2a4\ud14c\uc774\ud130\uc2a4\ub97c \ubc1b\ub294\ub2e4.\\n\uace4\ub780\ud558\uac8c\ub3c4 \ubcd1\uc774\ub77c\ub294 \uac83\uc740 \uc885\uc885 \uc0ac\ub78c\uc5d0\uc11c \uc0ac\ub78c\uc73c\ub85c \uc804\uc5fc\ub41c\ub2e4. \uc9c0\uc0c1\uc5d0\uc11c \uc720\ud589\ubcd1\uc774 \ub3cc\uc544\ub3c4 \ud070\uc77c\uc774\uac74\ub9cc, \ud558\ub298 \uc704\uc5d0\uc11c \uc774\ub7f0 \uc0c1\ud669\uc774 \ub418\uc5b4\ubc84\ub9ac\uba74 \uc5b4\ucc0c\ud560 \ubc29\ubc95\uc774 \uc5c6\ub2e4. \uc544\uc544, \ud558\ub298\uc740 \uc5ec\ub7ec\ubd84\uc744 \ubc84\ub9ac\ub294 \uac83\uc77c\uae4c?","\uae30\ub958+1\\n\ube44\uacf5\uc815 \ub9d0\uc744 1\uc2a4\ud3ff \uc774\ub3d9\uc2dc\ud0a8\ub2e4.\\n\ub410\ub2e4! \uc21c\ud48d\uc774 \ub418\uc5b4\uc904 \uae30\ub958\ub97c \ubc1c\uacac\ud588\ub2e4. \ud55c\uc2dc\ub77c\ub3c4 \ube68\ub9ac \ubaa9\uc801\uc9c0\uc5d0 \ub3c4\ucc29\ud574\uc57c\ub9cc \ud558\ub294 \uc5ec\ub7ec\ubd84\uc5d0\uac8c\ub294 \uadf8\uc57c\ub9d0\ub85c \uac00\ubb44 \uc18d\uc758 \ub2e8\ube44\ub2e4.","\ud558\ud544 \uc774\ub7f4 \ub54c\\n\uc7a5\uba74 \ud50c\ub808\uc774\uc5b4\ub294 \u300c\ud56d\ud589 \uc774\ubca4\ud2b8 \ud45c: \uace4\ub780\uacc4\u300d\uc5d0\uc11c \uc8fc\uc0ac\uc704 \uad74\ub9bc\uc744 \ud55c\ub2e4. \uad74\ub9b0 \uacb0\uacfc\uc5d0 \ud574\ub2f9\ud558\ub294 \ud56d\ud589 \uc774\ubca4\ud2b8\uc758 \ud6a8\uacfc\ub97c \uc801\uc6a9\ud55c\ub2e4.\\n\uc5ec\ub7ec\ubd84\uc774 \uc544\ubb34\ub9ac \uace0\uc0dd\ud558\uace0 \uc788\ub354\ub77c\ub3c4 \uc790\uc5f0\uc740 \uadf8\ub7f0 \uc0ac\uc815\uc744 \uc77c\uc77c\uc774 \ubd10\uc8fc\uc9c0 \uc54a\ub294\ub2e4. \uc2dc\ub828\uc740 \uac00\ucc28\uc5c6\uc774 \ub2e5\uccd0\uc624\uace0, \uc5ec\ub7ec\ubd84\uc744 \ub354 \ud070 \uace4\uacbd\uc5d0 \ube60\ub728\ub9b0\ub2e4.","\uc870\ub09c\\n\uc774 \ud56d\ud589 \uc774\ubca4\ud2b8\uac00 \ubc1c\uc0dd\ud55c \uc2dc\uc810\uc5d0\uc11c \uc138\uc158\uc744 \uc885\ub8cc\ud55c\ub2e4. \uc218\uace0\ud558\uc168\uc2b5\ub2c8\ub2e4.\\n\uadf8 \ud6c4, \uc5ec\ub7ec\ubd84\uc774 \ub2e4\uc2dc \u300e\ud0d1\u300f\uc5d0 \ub3cc\uc544\uc624\ub294 \uc77c\uc740 \uc5c6\uc5c8\ub2e4. \ud3ed\ud48d\uc5d0 \ud729\uc4f8\ub838\ub294\uc9c0, \uacf5\uc801\uc774\ub098 \ubd80\uc720\uc0dd\ubb3c\uc5d0\uac8c \uc2b5\uaca9\uc744 \ub2f9\ud588\ub294\uc9c0. \uadf8\uac83\uc744 \uc544\ub294 \uc774\ub294 \uc774\uc81c \uc5b4\ub514\uc5d0\ub3c4 \uc5c6\ub2e4."]}}}}')}}]);